type ColorsBase = {
  [key in CSSColors | CSSBgColors]: ((o?: string) => Colors) & Colors;
};

export interface Colors extends ColorsBase {
  color: (color: string) => Colors;
  bg: (color: string) => Colors;
  bgSize: (size: string) => Colors;
  log: (o: string) => Colors;
  style: (o: string) => Colors;
  padding: (a: number, b?: number) => Colors;
  fontsize: (size: number) => Colors;
  fontfamily: (family: string) => Colors;
  underline: () => Colors;
  linethrough: () => Colors;
  italic: () => Colors;
  bold: () => Colors;
}

export const colors: (() => Colors) & Colors;

export const logger: ((...args: (Colors | string)[]) => void) & {
  source: any;
};
